import React, { useState, useEffect } from 'react';
import { Card, Container, Table, Form, Button, Alert, Badge, Spinner } from 'react-bootstrap';
import { motion } from 'framer-motion';
import api from '../../services/api';
import { useAuth } from '../../context/AuthContext';

interface InventoryItem {
  id: number;
  name: string;
  category: string;
  quantity: number;
  threshold: number;
  department: string;
  last_updated: string;
}

interface FormData extends Omit<InventoryItem, 'id' | 'last_updated'> {
  id?: number; // Make id optional for the form
}

const StockControl: React.FC = () => {
  const { user } = useAuth();
  const [items, setItems] = useState<InventoryItem[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState<FormData>({
    name: '',
    category: 'stationery',
    quantity: 0,
    threshold: 5,
    department: 'general'
  });

  useEffect(() => {
    const fetchInventory = async () => {
      try {
        setLoading(true);
        const response = await api.get('/store/inventory/');
        setItems(response.data);
      } catch (err) {
        setError('Failed to fetch inventory');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchInventory();
  }, []);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'quantity' || name === 'threshold' ? parseInt(value) || 0 : value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      let updatedItems;
      if (formData.id) {
        // Update existing item
        const response = await api.put(`/store/inventory/${formData.id}/`, formData);
        updatedItems = items.map(item => 
          item.id === formData.id ? response.data : item
        );
      } else {
        // Add new item
        const response = await api.post('/store/inventory/', formData);
        updatedItems = [...items, response.data];
      }
      setItems(updatedItems);
      setShowForm(false);
      setFormData({
        name: '',
        category: 'stationery',
        quantity: 0,
        threshold: 5,
        department: 'general'
      });
    } catch (err) {
      setError('Failed to save inventory item');
      console.error(err);
    }
  };

  const handleRestock = async (id: number) => {
    try {
      const response = await api.patch(`/store/inventory/${id}/restock/`, { amount: 10 });
      setItems(items.map(item => 
        item.id === id ? response.data : item
      ));
    } catch (err) {
      setError('Failed to restock item');
      console.error(err);
    }
  };

  if (loading) {
    return (
      <Container className="py-4 text-center">
        <Spinner animation="border" variant="primary" />
      </Container>
    );
  }

  if (error) {
    return (
      <Container className="py-4">
        <Alert variant="danger">{error}</Alert>
      </Container>
    );
  }

  return (
    <Container className="py-4">
      <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
        <Card className="mb-4">
          <Card.Header className="d-flex justify-content-between align-items-center">
            <h3>Inventory Management</h3>
            <Button variant="primary" onClick={() => setShowForm(!showForm)}>
              {showForm ? 'Cancel' : 'Add New Item'}
            </Button>
          </Card.Header>
          <Card.Body>
            {showForm && (
              <Form onSubmit={handleSubmit} className="mb-4">
                <Form.Group className="mb-3">
                  <Form.Label>Item Name</Form.Label>
                  <Form.Control
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                  />
                </Form.Group>
                {/* Add other form fields here */}
                <Button variant="primary" type="submit">
                  Save
                </Button>
              </Form>
            )}

            <Table striped bordered hover responsive>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Category</th>
                  <th>Quantity</th>
                  <th>Threshold</th>
                  <th>Department</th>
                  <th>Last Updated</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {items.map(item => (
                  <tr key={item.id}>
                    <td>{item.name}</td>
                    <td>{item.category}</td>
                    <td>
                      {item.quantity}
                      {item.quantity <= item.threshold && (
                        <Badge bg="danger" className="ms-2">Low</Badge>
                      )}
                    </td>
                    <td>{item.threshold}</td>
                    <td>{item.department}</td>
                    <td>{new Date(item.last_updated).toLocaleString()}</td>
                    <td>
                      <Button 
                        variant="warning" 
                        size="sm" 
                        className="me-2"
                        onClick={() => {
                          setFormData({
                            id: item.id,
                            name: item.name,
                            category: item.category,
                            quantity: item.quantity,
                            threshold: item.threshold,
                            department: item.department
                          });
                          setShowForm(true);
                        }}
                      >
                        Edit
                      </Button>
                      <Button 
                        variant="success" 
                        size="sm"
                        onClick={() => handleRestock(item.id)}
                        disabled={item.quantity > item.threshold}
                      >
                        Restock
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </Card.Body>
        </Card>
      </motion.div>
    </Container>
  );
};

export default StockControl;
